method.body:
				
				function Constructor(argobj)
				{
					if(argobj.pid==null) return false;
					var oCurObj = document.getElementById(argobj.pid);
					if(oCurObj==null) return false;
					var oParams = g_oParams.selectSingleNode("./param[@objectid='"+argobj.pid+"']");
					if(oParams==null) return false;
					var bStandard = oParams.selectSingleNode("standard").text=="yes" ? true : false;
					var bNavLock = oParams.selectSingleNode("nav_lock").text=="yes" ? true : false;
					var bShowSet = oParams.selectSingleNode("show_settings").text=="yes" ? true : false;
					var bShowStat = oParams.selectSingleNode("show_stat").text=="yes" ? true : false;
					var bShowMain = oParams.selectSingleNode("show_mainobj").text=="yes" ? true : false;
					var bShowAll = oParams.selectSingleNode("show_allobj").text=="yes" ? true : false;
					var oHeader = document.getElementById(argobj.pid+"_header");
					if(oHeader==null) return false;
					var aTmp = oHeader.getElementsByTagName("p");
					if(aTmp.length!=0) {
						//fixing Gecko-specific p rendering
						for(var i=0;i<aTmp.length;i++) {
							aTmp[i].style.padding = "0px";
							aTmp[i].style.margin = "0px";
						}
					}
					oCurObj.style.left = bStandard ? "-475px" : "-"+oParams.selectSingleNode("tab_width").text+"px";
					oCurObj.style.zIndex = 990;
					oCurObj.setAttribute("closed","1");
					oCurObj.setAttribute("state","enabled");
					oCurObj.setAttribute("navlock", bNavLock ? "1" : "0");

					if(bShowMain) {
						var sScoreStyle = oParams.selectSingleNode("score_style").text;
						var oMainObjDiv = document.getElementById(argobj.pid+"_mainobjdiv");
						if(oMainObjDiv==null) return false;
						var oMainObj = g_oSCO.selectSingleNode("objectives/o[@module='yes']");
						if(oMainObj==null) return false;
						oMainObjDiv.style.display = g_isMSIE ? "inline" : "block";
						var oHeader = document.getElementById(argobj.pid+"_mainobjheader");
						if(oHeader!=null) {
							var aTmp = oHeader.getElementsByTagName("p");
							if(aTmp.length!=0) {
							//fixing Gecko-specific p rendering
								for(var i=0;i<aTmp.length;i++) {
									aTmp[i].style.padding = "0px";
									aTmp[i].style.margin = "0px";
								}
							}
						}
						var sDesc = oMainObj.getAttribute("desc");
						if(sDesc==null || sDesc=="") sDesc = oMainObj.getAttribute("id");
						document.getElementById(argobj.pid+"_mainobjname").innerHTML = sDesc;

						if(sScoreStyle=="scaled") {
							var sScoreScaled = oMainObj.getAttribute("scaled");
							var nScore = parseFloat(sScoreScaled);
							if(isNaN(nScore)) {
								nScore = 0;
							} else {
								nScore = Math.round(nScore*10000)/100;
							}
							var sScore = nScore.toString() + "%";
						} else {
							var sScoreRaw = oMainObj.getAttribute("raw");
							var nScore = parseFloat(sScoreRaw);
							if(isNaN(nScore)) {
								nScore = 0;
							}
							var sScore = nScore.toString();
							if(sScoreStyle=="rawmax") {
								var sScoreMax = oMainObj.getAttribute("max");
								nScore = parseFloat(sScoreMax);
								if(isNaN(nScore)) {
									nScore = 0;
								}
								sScore += "(" + nScore.toString() + ")";
							}
						}
						document.getElementById(argobj.pid+"_mainobjscore").innerHTML = sScore;
					}

					if(bShowAll) {
						var sScoreStyle = oParams.selectSingleNode("score_style").text;
						var oAllObjDiv = document.getElementById(argobj.pid+"_allobjdiv");
						if(oAllObjDiv==null) return false;
						var oAllObjTable = document.getElementById(argobj.pid+"_allobjtable");
						if(oAllObjTable.firstChild.nodeName.toLowerCase()=="tbody") oAllObjTable = oAllObjTable.firstChild;
						var oTmpl = document.getElementById(argobj.pid+"_objtmpl");
						if(oAllObjTable==null || oTmpl==null) return false;
						var aObj = g_oSCO.selectNodes("objectives/o");
						if(aObj.length==0) return false;
						oAllObjDiv.style.display = g_isMSIE ? "inline" : "block";
						var oHeader = document.getElementById(argobj.pid+"_allobjheader");
						if(oHeader!=null) {
							var aTmp = oHeader.getElementsByTagName("p");
							if(aTmp.length!=0) {
							//fixing Gecko-specific p rendering
								for(var i=0;i<aTmp.length;i++) {
									aTmp[i].style.padding = "0px";
									aTmp[i].style.margin = "0px";
								}
							}
						}

						var sDesc, sScore, sScoreRaw, sScoreScaled, sScoreMax;
						var oClone;
						var aTags;
						var nScore;
						for(var i=0;i<aObj.length;i++) {
							if(aObj[i].getAttribute("module")=="yes") continue;
							oClone = oTmpl.cloneNode(true);
							aTags = oClone.getElementsByTagName("img");
							aTags[0].setAttribute("id", argobj.pid+"_ssimg_"+i.toString());
							aTags[1].setAttribute("id", argobj.pid+"_csimg_"+i.toString());
							aTags = oClone.getElementsByTagName("td");
							sDesc = aObj[i].getAttribute("desc");
							if(sDesc==null || sDesc=="") sDesc = aObj[i].getAttribute("id");
							aTags[2].innerHTML = sDesc;
							if(sScoreStyle=="scaled") {
								sScoreScaled = aObj[i].getAttribute("scaled");
								nScore = parseFloat(sScoreScaled);
								if(isNaN(nScore)) {
									nScore = 0;
								} else {
									nScore = Math.round(nScore*10000)/100;
								}
								sScore = nScore.toString() + "%";
							} else {
								sScoreRaw = aObj[i].getAttribute("raw");
								nScore = parseFloat(sScoreRaw);
								if(isNaN(nScore)) {
									nScore = 0;
								}
								sScore = nScore.toString();
								if(sScoreStyle=="rawmax") {
									sScoreMax = aObj[i].getAttribute("max");
									nScore = parseFloat(sScoreMax);
									if(isNaN(nScore)) {
										nScore = 0;
									}
									sScore += "(" + nScore.toString() + ")";
								}
							}
							aTags[3].innerHTML = sScore;
							aTags[3].setAttribute("id",argobj.pid+"_score_"+i.toString());
							oAllObjTable.appendChild(oClone);
						}
					}

					AddEvtHandler("EVENT_SLIDE_OPENED", "nav_006_settings", argobj.pid);
					AddEvtHandler("EVENT_SLIDE_COMPLETE", "nav_006_settings", argobj.pid);
					AddEvtHandler("EVENT_NAVIGATION", "nav_006_settings", argobj.pid);
					return true;
				}

				
				function UpdateContents(argobj)
				{
					if(argobj.pid==null) return false;
					var oCurObj = document.getElementById(argobj.pid);
					if(oCurObj==null) return false;
					var oParams = g_oParams.selectSingleNode("./param[@objectid='"+argobj.pid+"']");
					if(oParams==null) return false;
					var bStandard = oParams.selectSingleNode("standard").text=="yes" ? true : false;
					var bShowMain = oParams.selectSingleNode("show_mainobj").text=="yes" ? true : false;
					var bShowAll = oParams.selectSingleNode("show_allobj").text=="yes" ? true : false;
					var sScoreStyle = oParams.selectSingleNode("score_style").text;

					var sCS, sSS, sScore, sScoreRaw, sScoreScaled, sScoreMax;
					var oTD, oImg, oImgNew;
					var nScore;

					if(bShowMain) {
						var oMainObjDiv = document.getElementById(argobj.pid+"_mainobjdiv");
						if(oMainObjDiv==null) return false;
						var oMainObj = g_oSCO.selectSingleNode("objectives/o[@module='yes']");
						if(oMainObj==null) return false;

						if(sScoreStyle=="scaled") {
							sScoreScaled = oMainObj.getAttribute("scaled");
							nScore = parseFloat(sScoreScaled);
							if(isNaN(nScore)) {
								nScore = 0;
							} else {
								nScore = Math.round(nScore*10000)/100;
							}
							sScore = nScore.toString() + "%";
						} else {
							sScoreRaw = oMainObj.getAttribute("raw");
							nScore = parseFloat(sScoreRaw);
							if(isNaN(nScore)) {
								nScore = 0;
							}
							sScore = nScore.toString();
							if(sScoreStyle=="rawmax") {
								sScoreMax = oMainObj.getAttribute("max");
								nScore = parseFloat(sScoreMax);
								if(isNaN(nScore)) {
									nScore = 0;
								}
								sScore += "(" + nScore.toString() + ")";
							}
						}
						document.getElementById(argobj.pid+"_mainobjscore").innerHTML = sScore;
						sSS = oMainObj.getAttribute("ss");
						sCS = oMainObj.getAttribute("cs");
						if(sSS==null || sSS=="") sSS = "u";
						oImg = document.getElementById(argobj.pid+"_mainobj_ss_img");
						oImgNew = document.getElementById(argobj.pid+"_obj_"+sSS);
						oImg.src = oImgNew.src;
						oImg.alt = oImgNew.alt;
						if(sCS==null || sCS=="") sCS = "n";
						oImg = document.getElementById(argobj.pid+"_mainobj_cs_img");
						oImgNew = document.getElementById(argobj.pid+"_obj_"+sCS);
						oImg.src = oImgNew.src;
						oImg.alt = oImgNew.alt;
					}

					if(bShowAll) {
						var oAllObjDiv = document.getElementById(argobj.pid+"_allobjdiv");
						if(oAllObjDiv==null) return false;
						var aObj = g_oSCO.selectNodes("objectives/o");
						if(aObj.length==0) return false;
						for(var i=0;i<aObj.length;i++) {
							if(aObj[i].getAttribute("module")=="yes") continue;
							oTD = document.getElementById(argobj.pid+"_score_"+i.toString());
							if(oTD==null) continue;
							if(sScoreStyle=="scaled") {
								sScoreScaled = aObj[i].getAttribute("scaled");
								nScore = parseFloat(sScoreScaled);
								if(isNaN(nScore)) {
									nScore = 0;
								} else {
									nScore = Math.round(nScore*10000)/100;
								}
								sScore = nScore.toString() + "%";
							} else {
								sScoreRaw = aObj[i].getAttribute("raw");
								nScore = parseFloat(sScoreRaw);
								if(isNaN(nScore)) {
									nScore = 0;
								}
								sScore = nScore.toString();
								if(sScoreStyle=="rawmax") {
									sScoreMax = aObj[i].getAttribute("max");
									nScore = parseFloat(sScoreMax);
									if(isNaN(nScore)) {
										nScore = 0;
									}
									sScore += "(" + nScore.toString() + ")";
								}
							}
							oTD.innerHTML = sScore;
							sSS = aObj[i].getAttribute("ss");
							sCS = aObj[1].getAttribute("cs");
							if(sSS==null || sSS=="") sSS = "u";
							oImg = document.getElementById(argobj.pid+"_ssimg_"+i.toString());
							oImgNew = document.getElementById(argobj.pid+"_obj_"+sSS);
							oImg.src = oImgNew.src;
							oImg.alt = oImgNew.alt;
							if(sCS==null || sCS=="") sCS = "n";
							oImg = document.getElementById(argobj.pid+"_csimg_"+i.toString());
							oImgNew = document.getElementById(argobj.pid+"_obj_"+sCS);
							oImg.src = oImgNew.src;
							oImg.alt = oImgNew.alt;
						}
					}
					return true;
				}


function HandleEvt(oEvtHandler, oArg)
{
	var argobj = new Object;
	argobj.pid = oEvtHandler.sTargetId;
	var oCurObj = document.getElementById(argobj.pid);
	if(oCurObj==null) return false;
	var sCurrentSlideId = g_oSlide.getAttribute("id");
	var sSavedSlideId = oCurObj.getAttribute("sid");
	if(sSavedSlideId==null) sSavedSlideId = "";
	switch (oEvtHandler.sEvt) {
		case "EVENT_SLIDE_OPENED":
			if(sSavedSlideId!=sCurrentSlideId) oCurObj.setAttribute("sid","");
			if(oCurObj.getAttribute("closed")=="0") CallMethod("nav_006_settings","MoveTab",argobj);
			if(oCurObj.getAttribute("navlock")=="1") CallMethod("nav_006_settings","DisableAccess",argobj);
			break;
		case "EVENT_SLIDE_COMPLETE":
			if(oCurObj.getAttribute("state")=="disabled" && sSavedSlideId=="") {
				oCurObj.setAttribute("sid","");
				CallMethod("nav_006_settings","EnableAccess",argobj);
			}
			break;
		case "EVENT_NAVIGATION":
			if(oArg==null) return false;
			if(oArg.sTargets=="calc" || oArg.sTargets=="all") {
				if(oArg.sAction=="on" && oCurObj.getAttribute("state")=="disabled") {
					oCurObj.setAttribute("sid","");
					CallMethod("nav_006_settings","EnableAccess",argobj);
				}
				if(oArg.sAction=="off" && oCurObj.getAttribute("state")=="enabled") {
					oCurObj.setAttribute("sid",sCurrentSlideId);
					CallMethod("nav_006_settings","DisableAccess",argobj);
				}
			}
		default:
			break;
	}
	return true;
}


function OpenTab(argobj)
{
	if(argobj.pid==null) return false;
	var oCurObj = document.getElementById(argobj.pid);
	if(oCurObj==null) return false;
	var sCurrentSlideId = g_oSlide.getAttribute("id");
	var sSavedSlideId = oCurObj.getAttribute("sid");
	if(sCurrentSlideId==sSavedSlideId) return false;
	var oParams = g_oParams.selectSingleNode("./param[@objectid='"+argobj.pid+"']");
	var bStandard = oParams.selectSingleNode("standard").text=="yes" ? true : false;
	var iDockX = bStandard ? -475 : -parseInt(oParams.selectSingleNode("tab_width").text);
	var iY = oCurObj.offsetTop;
	var bOpenTab = oCurObj.getAttribute("closed")=="1" ? true : false;
	var dtBegin = new Date();
	if(bOpenTab) {
		CreateTimeActionMove(argobj.pid, 0, 300, iDockX, iY, 0, iY);
		CallMethod("nav_006_settings","UpdateContents",argobj);
		oCurObj.style.zIndex = 991;
		oCurObj.setAttribute("closed","0");
	}
	return true;
}


function CloseTab(argobj)
{
	if(argobj.pid==null) return false;
	var oCurObj = document.getElementById(argobj.pid);
	if(oCurObj==null) return false;
	var sCurrentSlideId = g_oSlide.getAttribute("id");
	var sSavedSlideId = oCurObj.getAttribute("sid");
	if(sCurrentSlideId==sSavedSlideId) return false;
	var oParams = g_oParams.selectSingleNode("./param[@objectid='"+argobj.pid+"']");
	var bStandard = oParams.selectSingleNode("standard").text=="yes" ? true : false;
	var iDockX = bStandard ? -475 : -parseInt(oParams.selectSingleNode("tab_width").text);
	var iY = oCurObj.offsetTop;
	var bOpenTab = oCurObj.getAttribute("closed")=="1" ? true : false;
	var dtBegin = new Date();
	if(!bOpenTab) {
		CreateTimeActionMove(argobj.pid, 0, 300, 0, iY, iDockX, iY);
		CreateTimeActionMethod("nav_006_settings", "sinkTab", null, 300, argobj);
		oCurObj.setAttribute("closed","1");
	}
	return true;
}


function MoveTab(argobj)
{
	if(argobj.pid==null) return false;
	var oCurObj = document.getElementById(argobj.pid);
	if(oCurObj==null) return false;
	var oParams = g_oParams.selectSingleNode("./param[@objectid='"+argobj.pid+"']");
	var bStandard = oParams.selectSingleNode("standard").text=="yes" ? true : false;
	var iDockX = bStandard ? -475 : -parseInt(oParams.selectSingleNode("tab_width").text);
	var iY = oCurObj.offsetTop;
	var bOpenTab = oCurObj.getAttribute("closed")=="1" ? true : false;
	var dtBegin = new Date();
	if(bOpenTab) {
		CreateTimeActionMove(argobj.pid, 0, 300, iDockX, iY, 0, iY);
		CallMethod("nav_006_settings","UpdateContents",argobj);
		oCurObj.style.zIndex = 991;
		oCurObj.setAttribute("closed","0");
	} else {
		CreateTimeActionMove(argobj.pid, 0, 300, 0, iY, iDockX, iY);
		CreateTimeActionMethod("nav_006_settings", "sinkTab", null, 300, argobj);
		oCurObj.setAttribute("closed","1");
	}
	return true;
}


function sinkTab(argobj)
{
	if(argobj.pid==null) return false;
	var oCurObj = document.getElementById(argobj.pid);
	if(oCurObj==null) return false;
	oCurObj.style.zIndex=990;
	return true;
}


function DisableAccess(argobj)
{
	if(argobj.pid==null) return false;
	var oCurObj = document.getElementById(argobj.pid);
	if(oCurObj==null) return false;
	var bOpenTab = oCurObj.getAttribute("closed")=="1" ? true : false;
	if(!bOpenTab) CallMethod("nav_005_calc","MoveTab",argobj);
	document.getElementById(argobj.pid+"_tabimg").style.display = "none";
	document.getElementById(argobj.pid+"_tabimg_disabled").style.display = g_isMSIE ? "inline" : "block";
	oCurObj.setAttribute("state","disabled");
	return true;
}


function EnableAccess(argobj)
{
	if(argobj.pid==null) return false;
	var oCurObj = document.getElementById(argobj.pid);
	if(oCurObj==null) return false;
	document.getElementById(argobj.pid+"_tabimg_disabled").style.display = "none";
	document.getElementById(argobj.pid+"_tabimg").style.display = g_isMSIE ? "inline" : "block";
	oCurObj.setAttribute("state","enabled");
	return true;
}


function GetProperty(argobj)
{
	if(argobj.pid==null || argobj.property==null) return false;
	var oCurObj = document.getElementById(argobj.pid);
	if(oCurObj==null) return false;
	switch(argobj.property.toLowerCase()) {
		case "disabled":	g_vPropertyValue = oCurObj.getAttribute("state")=="disabled" ? "1" : "0";	break;
		case "open":		g_vPropertyValue = oCurObj.getAttribute("closed")=="1" ? "1" : "0";	break;
		default: return true;
	}
	return true;
}
